import {fetchData} from './api';

class App extends React.Component {

  state = {
    data: {},
  }

  async componentDidMount(){
    const fetchData = await fetchData();

    this.setState({data: data});
  }

  render() {
    const {data} = this.state;

    return (
      <div className={styles.container}>
        <Cards data={data}/>
        <Chart />
        <CountryPicker />
      </div>
    )
  }
}

export default App;
